definition.script:
def label = "purepod-${UUID.randomUUID().toString()}"
def DOCKER_URL = "${params.DOCKER_REPO_URL}"
def GITLAB_URL = "${params.GITLAB_URL}"
def GIT_CREDENTIALS_ID = "${params.GIT_CREDENTIALS_ID}"
def NEXUS_CREDENTIALS_ID = "${params.NEXUS_CREDENTIALS_ID}"
def NEXUS_URL = "${params.NEXUS_URL}"

// Create pod template using desired image.
podTemplate(label: label, containers: [
    containerTemplate(name: 'jnlp', image: DOCKER_URL + '/jnlp-slave-sudo', alwaysPullImage: true,privileged: true, args: '${computer.jnlpmac} ${computer.name}', workingDir: '/home/jenkins/dev-jenkins'),
    containerTemplate(name: 'buildubuntu', image: DOCKER_URL + '/build-kernel', alwaysPullImage: true,privileged: true, workingDir: '/home/jenkins/dev-jenkins',  ttyEnabled: true, command: 'cat')
    ], 
    volumes: [
    persistentVolumeClaim(mountPath: '/home/jenkins/dev-jenkins/workspace/' + env.JOB_NAME, claimName: 'pso-fb', readOnly: false)
    ],
    imagePullSecrets: [ 'myregistrykey' ])

    {
    node(label) {
        stage('Clone Repository') {
            container('buildubuntu') {
                dir(env.BUILD_NUMBER + '/build') {
                    git branch: 'master', credentialsId: GIT_CREDENTIALS_ID, url: GITLAB_URL + '/root/ubuntu-xenial.git'
                }
            }
        }

        stage('Build project') {
            container('buildubuntu') {
                dir(env.BUILD_NUMBER + '/build') {
                sh 'fakeroot debian/rules clean'
                sh 'fakeroot debian/rules binary-headers binary-generic binary-perarch'
                }
            }
        }

        // Create tar.gz package.   
        stage('Create Package') {
            container('buildubuntu') {
                dir(env.BUILD_NUMBER + '/build') {
                sh "tar -czvf kern-package.tar.gz ../*.deb"
                }
            }
        }

        // Upload to Nexus
        stage('Add to Nexus artifacts') {
            container('buildubuntu') {
                dir(env.BUILD_NUMBER + '/build') {
                    nexusArtifactUploader artifacts: [[artifactId: 'kl_artId1', classifier: 'kern-package', file: 'kern-package.tar.gz', type: 'tar.gz']], credentialsId: NEXUS_CREDENTIALS_ID, groupId: 'Kernel', nexusUrl: NEXUS_URL, nexusVersion: 'nexus3', protocol: 'http', repository: 'kernel', version: '1.0.0.'+env.BUILD_NUMBER
                }
            }
        }
    }
  }
