definition.script:
def label = "mypod-${UUID.randomUUID().toString()}"
def DOCKER_URL = "${params.DOCKER_REPO_URL}"
def GITLAB_URL = "${params.GITLAB_URL}"
def NEXUS_URL = "${params.NEXUS_URL}"
def WP_BUILD_NUMBER = "${params.WP_BUILD_NUMBER}"

def VSPHERE_HOST = '{{VSPHERE_HOST}}'
def VSPHERE_RESOURCE = '{{VSPHERE_RESOURCE}}'
def VM_TEMPLATE = '{{VM_TEMPLATE}}'
def VSPHERE_USER = '{{VSPHERE_USER}}'
def VSPHERE_PASSWORD = '{{VSPHERE_PASSWORD}}'
def VM_MEMORY = {{VM_MEMORY}}
def VM_CPU = {{VM_CPU}}
def VM_COUNT = "${params.NO_OF_VMS}"

// Create pod template using desired image.
podTemplate(label: label, containers: [
    containerTemplate(name: 'jnlp', image: DOCKER_URL + '/jnlp-slave-sudo', alwaysPullImage: true, args: '${computer.jnlpmac} ${computer.name}', workingDir: '/home/jenkins/dev-jenkins'),
    containerTemplate(name: 'wordpress-cd', image: DOCKER_URL + '/purestorage/vagrant:latest', alwaysPullImage: true, workingDir: '/home/jenkins/dev-jenkins',  ttyEnabled: true, command: 'cat')
    ], 

    //mounting the Pure Storage FlashBlade volume on pod
    volumes: [
    persistentVolumeClaim(mountPath: '/home/jenkins/dev-jenkins/workspace/' + env.JOB_NAME, claimName: 'fb-jenkins-slave', readOnly: false)
    ],
    imagePullSecrets: [ 'jenkins-pull' ]) {
    node(label) {
        //Installing the vagrant plugin on container
        container('wordpress-cd'){
            println 'Stage 2 - CD'
            dir(env.BUILD_NUMBER + '/build') {

                if ((env.BUILD_ID.toInteger() % {{MASTER_BUILD_NO}}) == 0) {
                    //cloning the stage-cd repo having vagrantfile and bootstrap script                
                    stage('Clone Repository') {
                       git branch: 'master', credentialsId: 'gitlab' , url: GITLAB_URL + '/root/wordpress-cd.git'
                    }


                    sh 'vagrant plugin install vagrant-vsphere > /dev/null'

                    dir('stage-2'){
                         stage('Launch VM') {
                            sh "sed -i 's/@@BUILD_NUMBER@@/${BUILD_NUMBER}/g' Vagrantfile"
                            sh "sed -i 's/@@VSPHERE_HOST@@/${VSPHERE_HOST}/g' Vagrantfile"
                            sh "sed -i 's/@@VSPHERE_RESOURCE@@/${VSPHERE_RESOURCE}/g' Vagrantfile"
                            sh "sed -i 's/@@VM_TEMPLATE@@/${VM_TEMPLATE}/g' Vagrantfile"
                            sh "sed -i 's/@@VSPHERE_USER@@/${VSPHERE_USER}/g' Vagrantfile"
                            sh "sed -i 's/@@VSPHERE_PASSWORD@@/${VSPHERE_PASSWORD}/g' Vagrantfile"

                            sh "sed -i 's/@@NEXUS_URL@@/${NEXUS_URL}/g' upgrade-wp.sh"
                            sh "sed -i 's/@@WP_BUILD_NUMBER@@/${WP_BUILD_NUMBER}/g' upgrade-wp.sh"

                            //sh 'cat Vagrantfile'
                            //sh 'cat upgrade-wp.sh'    

                            sh 'vagrant up --provider=vsphere'
                        }
                    }


                    dir('stage-3') {
                        stage('Clone Stage 2 VM and Execute Queries') {
                            sh "sed -i 's/@@BUILD_NUMBER@@/${BUILD_NUMBER}/g' Vagrantfile"
                            sh "sed -i 's/@@VSPHERE_HOST@@/${VSPHERE_HOST}/g' Vagrantfile"
                            sh "sed -i 's/@@VSPHERE_RESOURCE@@/${VSPHERE_RESOURCE}/g' Vagrantfile"
                            sh "sed -i 's/@@VM_TEMPLATE@@/${VM_TEMPLATE}/g' Vagrantfile"
                            sh "sed -i 's/@@VSPHERE_USER@@/${VSPHERE_USER}/g' Vagrantfile"
                            sh "sed -i 's/@@VSPHERE_PASSWORD@@/${VSPHERE_PASSWORD}/g' Vagrantfile"
                            sh "sed -i 's/@@VM_MEMORY@@/${VM_MEMORY}/g' Vagrantfile"
                            sh "sed -i 's/@@VM_CPU@@/${VM_CPU}/g' Vagrantfile"
                            sh "sed -i 's/@@VM_COUNT@@/${VM_COUNT}/g' Vagrantfile"

                            sh 'cat Vagrantfile'

                            sh 'vagrant up --provider=vsphere'
                        }
                    }


                } else {
                    echo 'WordPress CD Stage will not be trigged as this is not a GOLD build'
                }
            }
        }
    }
}
