definition.script:
def label = "purepod-${UUID.randomUUID().toString()}"
def DOCKER_REPO_URL = "${params.DOCKER_REPO_URL}"
def GITLAB_URL = "${params.GITLAB_URL}"
def NEXUS_URL = "${params.NEXUS_URL}"

// Create pod template using desired image.
podTemplate(label: label, containers: [
        containerTemplate(name: 'jnlp', image: DOCKER_REPO_URL + '/jnlp-slave-sudo', alwaysPullImage: true, args: '${computer.jnlpmac} ${computer.name}', workingDir: '/home/jenkins/dev-jenkins'),
        containerTemplate(name: 'buildwordpress', image: DOCKER_REPO_URL + '/build-wordpress', workingDir: '/home/jenkins/dev-jenkins', ttyEnabled: true, command: 'cat')],
    volumes: [
        //persistentVolumeClaim(mountPath: '/home/jenkins/dev-jenkins/workspace/' + env.JOB_NAME, claimName: 'fb-jenkins-slave', readOnly: false)],
        persistentVolumeClaim(mountPath: '/home/jenkins/dev-jenkins'  + env.JOB_NAME, claimName: 'fb-jenkins-slave', readOnly: false)],
    imagePullSecrets: [ 'jenkins-pull' ]) 

    {
    node(label) {
    container('buildwordpress') {
            // Git clone source code.
            stage('Clone Repository') {
                dir(env.BUILD_NUMBER + '/build') {
                git branch: 'master', credentialsId: 'gitlab', url: GITLAB_URL + '/root/wordpress-develop.git'
                }

            }
            // Install dependancies and build project.
            stage('Build project') {
                dir(env.BUILD_NUMBER + '/build') {
                    sh "npm install"
                    sh "grunt build"
                }
            }
            // Run QUnit test cases
            stage('Run Unit Tests') {
                dir(env.BUILD_NUMBER + '/build') {
                    sh "grunt qunit:reports --force"
                }
            }
            // Create tar.gz package
            stage('Create Package') {
                dir(env.BUILD_NUMBER + '/build') {
                    sh "mkdir wordpress" 
                    sh "cp -rf build/* wordpress/"
                    sh "tar -czvf Wordpress.tar.gz wordpress/"
                }
            }
            stage('Add to Nexus artifacts') {
                dir(env.BUILD_NUMBER + '/build') {
                    nexusArtifactUploader artifacts: [[artifactId: 'wordpress-artifacts', classifier: 'wordpress-artifacts', file: 'Wordpress.tar.gz', type: 'tar.gz']], credentialsId: 'nexus', groupId: 'wp_gid', nexusUrl: NEXUS_URL, nexusVersion: 'nexus3', protocol: 'http', repository: 'builds-store', version: '1.0.0.'+env.BUILD_NUMBER
                }
            }
        }
    }
}
